                //    db.SaveChanges();
                //}


                var query = (from s in db.DBLotes
                               where s.COD_LOTE == cod_lote
                               select s);

                foreach(LOTES lotesUpdatatzeko in query)
                {
                    lotesUpdatatzeko.CODALMACEN = codAlmacen;
                    db.SaveChanges();
                }

            }

            
