    public class JoinKlas
    {
        [Key, Column(Order = 0)]
        public int COD_LOTE { get; set; }
        public string LOTE { get; set; }
        //[ForeignKey("ALMACENESCH")]
        public string CODALMACEN { get; set; }
        public string IDENT { get; set; }
